public ServiceA serviceA(RestTemplateBuilder builder) {
    return new ServiceA(builder.basicAuthorization("sa", "sa"));
}

@Bean
public ServiceB serviceB(RestTemplateBuilder builder) {
    return new ServiceB(builder.defaultMessageConverters());
